mapper.select:
select a.id,caption,assetId,state,time startTime,auto,errorMessage,handled,(select time from
db_vibe_monitor.t_error_message_log t where t.assetId = e.assetId order by t.id desc limit 0,1) endTime from
db_vibe_monitor.t_alarm_message a,db_vibe_monitor.t_error_message_log e
where a.logId = e.id and auto = 1

    and e.time >= #{start}


    and e.time <= #{end}

union select a.id,caption,assetId,state,time startTime,auto,errorMessage,handled,endTime from
db_vibe_monitor.t_alarm_message a,db_vibe_monitor.t_error_message_log e
where a.logId = e.id and auto = 2

    and e.time >= #{start}


    and e.time <= #{end}

order by endTime desc limit #{pageNum},#{pageCount}

select * from ( select a.id,caption,assetId,state,time startTime,auto,errorMessage,handled,(select endTime from db_vibe_monitor.t_error_message_log t where t.state = e.state order by t.id desc limit 0,1) endTime from db_vibe_monitor.t_alarm_message a,db_vibe_monitor.t_error_message_log e where a.logId = e.id and auto = 1 union select a.id,caption,assetId,state,time startTime,auto,errorMessage,handled,endTime from db_vibe_monitor.t_alarm_message a,db_vibe_monitor.t_error_message_log e where a.logId = e.id and auto = 2 ) as list
and DATE_FORMAT(list.startTime,'%Y-%m-%d %H:%i:%S') >= #{start}
and DATE_FORMAT(list.startTime,'%Y-%m-%d %H:%i:%S') <= #{end}
and list.handled =#{handled}
order by list.startTime desc

select DATE_FORMAT(t1.time,'%H') as time, count(t1.id) as num from db_vibe_monitor.t_error_message_log t1
and t1.state = #{state}
and UNIX_TIMESTAMP(t1.time) >= UNIX_TIMESTAMP(DATE_FORMAT(#{time},'%Y-%m-%d')) and UNIX_TIMESTAMP(t1.time) <= UNIX_TIMESTAMP(#{time})
group by DATE_FORMAT(t1.time,'%Y-%m-%d %H') order by DATE_FORMAT(t1.time,'%Y-%m-%d %H');
