mapper.select:
select
t.unit
from db_vibe_data.t_sample_float_day d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where e.value_type=1 and d.monitor in
#{id}
and d.moment like #{date}
and s.id in
#{parent}
group by(unit)

select s.caption
from db_vibe_data.t_sample_float_day d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where e.value_type=1 and d.monitor in
#{id}
and d.moment like #{date}
and s.id in
#{parent}
group by(unit)

select d.monitor, d.moment, sum(d.avg) AS avg, e.itemize_type, t.name, s.caption, t.unit, p.parent from db_vibe_data.t_sample_float_hour d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on s.id=p.parent
where e.value_type=1 and d.monitor in
#{id}
and d.moment >= #{sTime} and d.moment <= #{eTime}
and p.parent = #{parent}

select
avg(amountValue) as avgNum
from db_vibe_data.t_sample_float_day d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where s.id = #{parent} and e.value_type=1 and d.moment like #{date} and e.itemize_type=#{type}

select d.monitor , avg(d.avg ) as avgNumNight from db_vibe_data.t_sample_float_hour d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on s.id=p.parent where d.monitor in
#{id}
and d.moment >= #{sTime} and d.moment <= #{eTime} and e.value_type=1 and s.id = #{parent} group by(d.monitor);

SELECT truncate(amountValue,2) as value, d.moment as time, t.unit as unit,s.caption as floor,t.name FROM db_vibe_data.${tableName} d LEFT JOIN db_vibe_monitor.t_probe_energy e ON d.monitor = e.probe_id LEFT JOIN db_vibe_monitor.t_probe p ON d.monitor = p.id LEFT JOIN db_vibe_basic.t_code t ON t.id = e.itemize_type LEFT JOIN db_vibe_monitor.t_space s ON p.parent = s.id
e.value_type=1 and p.parent = ${id}
and itemize_type = ${catalog}
and d.moment between '${startTime}' and '${lastTime}'
and d.moment like '${startTime}%'
ORDER BY d.moment ASC

SELECT (amountValue) AS yongdianlaing ,d.moment AS shijian FROM db_vibe_data.${tableName} d LEFT JOIN db_vibe_monitor.t_probe_energy e ON d.monitor = e.probe_id LEFT JOIN db_vibe_monitor.t_probe p ON d.monitor = p.id LEFT JOIN db_vibe_basic.t_code t ON t.id = e.itemize_type LEFT JOIN db_vibe_monitor.t_space s ON p.parent = s.id where s.id= ${floor} and itemize_type = ${catalog} and e.value_type=1
and d.moment between '${startTime}' and '${lastTime}'
and d.moment like '${startTime}%'
group by d.moment
order by d.moment ${rank}
order by yongdianlaing ${rank}
