mapper.select:
select amountValue value , moment from db_vibe_data.${tableName}

    monitor=${id}

        and substr(moment,1,${subNum})='${nowTime}'



select sum(amountValue) value from db_vibe_data.${tableName}

    monitor=${id}

        and moment between '${startTime}' and '${lastTime}'


        and substr(moment,1,${subNum})='${startTime}'

select amountValue value , moment from db_vibe_data.${tableName}

    monitor=${id}

        and moment between '${startTime}' and '${lastTime}'


        and substr(moment,1,${subNum})='${startTime}'

select monitor monitorId,sum(value) value,moment,sum(carbon) carbon
from db_vibe_data.t_sample_float

where monitor = #{id} and moment between #{startTime} and #{lastTime}

select monitor monitorId,sum(carbon) carbon,substring(moment,6,2) moment

from db_vibe_data.t_sample_float


        monitor = #{monitorId}


        and moment >= #{startTime}


        and moment between #{startTime} and #{lastTime}


group by SUBSTRING(moment,6,2)

select d.avg AS value ,d.moment AS Time ,t.unit from db_vibe_data.${tableName} d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id
and p.parent = ${ids} and e.value_type=1 and itemize_type = ${catalog}
and d.moment between '${startTime}' and '${lastTime}'

select d.avg AS value ,d.moment AS Time,t.unit from db_vibe_data.${tableName} d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where p.parent = ${ids} and e.value_type=1 and e.itemize_type = ${catalog}
and d.moment like '${startTime}'
order by d.moment asc

select d.avg AS value ,d.moment AS Time,t.unit from db_vibe_data.${tableName} d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id=e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where p.parent = ${ids} and e.value_type=1 and e.itemize_type = ${catalog}
and d.moment like '${startTime}'
order by value asc

select sum(d.avg) AS value,t.unit from db_vibe_data. ${tableName} d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id = e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where e.value_type=1 and e.itemize_type in ${id}
and d.moment like '${startTime}'
and p.parent = 1

select sum(d.avg) AS value,t.unit from db_vibe_data. ${tableName} d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id = e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where e.value_type=1 and e.itemize_type in ${id}
and d.moment between '${startTime}' and '${lastTime}'
and p.parent = 1

select avg(d.avg) AS avg,t.name,t.unit from db_vibe_data.${tableName} d left join db_vibe_monitor.t_probe_energy e on d.monitor=e.probe_id left join db_vibe_monitor.t_probe p on d.monitor=p.id left join db_vibe_basic.t_code t on t.id = e.itemize_type left join db_vibe_monitor.t_space s on p.parent=s.id where e.value_type=1 and e.itemize_type in ${id}
and d.moment like '${startTime}'
and p.parent = 1 group by name

SELECT sum(amountValue) as value,t.name,t.unit FROM db_vibe_data.${tableName} d LEFT JOIN db_vibe_monitor.t_probe_energy e ON d.monitor = e.probe_id LEFT JOIN db_vibe_monitor.t_probe p ON d.monitor = p.id LEFT JOIN db_vibe_basic.t_code t ON t.id = e.itemize_type where e.value_type=1 and t.id = ${id}
and d.moment between '${startTime}' and '${lastTime}'
and d.moment like '${startTime}%'

