from db_vibe_data.t_sample_float


        monitor = #{monitorId}


        and moment >= #{startTime}


        and moment between #{startTime} and #{lastTime}


order by moment

select value , moment
from db_vibe_data.t_sample_float


        monitor = #{monitorId}


        and moment >= #{startTime}


        and moment between #{startTime} and #{lastTime}


order by moment

select value, moment
from db_vibe_data.t_sample_int


        monitor = #{monitorId}


        and moment >= #{startTime}


        and moment <= #{lastTime}


order by moment

select value, moment
from db_vibe_data.t_sample_boolean


        monitor = #{monitorId}


        and moment >= #{startTime}


        and moment <= #{lastTime}


order by moment

select value, moment
from db_vibe_data.t_sample_float


        monitor = #{monitorId}


        and moment >= #{startTime}


        and moment <= #{lastTime}


order by moment

select DATE_FORMAT(t1.moment,'%H:00') as time, ROUND(avg(t1.`value`),2) as value from db_vibe_data.t_sample_float t1 left join db_vibe_monitor.t_probe t2 on t1.monitor = t2.id
and t2.catalog =#{code}
and UNIX_TIMESTAMP(t1.moment) >= UNIX_TIMESTAMP(DATE_FORMAT(#{date},'%Y-%m-%d')) and UNIX_TIMESTAMP(t1.moment) <= UNIX_TIMESTAMP(#{date}) GROUP BY DATE_FORMAT(t1.moment,'%Y-%m-%d %H') order by DATE_FORMAT(t1.moment,'%Y-%m-%d %H');
