mapper.select:
select * from db_vibe_monitor.t_scheduled_task where 1 = 1

    and id = #{id}


    and type != 0


    and type = #{type}


    and state = #{state}


    and createPerson = #{createPerson}


    and executePerson = #{executePerson}

order by id desc

mapper.update:
update db_vibe_monitor.t_scheduled_task set type = #{type},timeExpression = #{timeExpression},state =
#{state},name = #{name},description = #{description},createPerson = #{createPerson},executePerson =
#{executePerson},grade = #{grade} where id = #{id}

mapper.select:
select * from db_vibe_monitor.t_task where 1=1

    and id = #{id}


    and type = #{type}


    and state = #{state}


    and scheduledTaskId = #{scheduledTaskId}


    and createPerson = #{createPerson}


    and executePerson = #{executePerson}


    and startStop = #{startStop}

order by id desc

mapper.delete:
delete from db_vibe_monitor.t_task where 1 = 1

    and id = #{id}


    and type = #{type}


    and state = #{state}


    and scheduledTaskId = #{scheduledTaskId}


    and createPerson = #{createPerson}


    and executePerson = #{executePerson}


    and startStop = #{startStop}
